using System;\nusing System.IO;\nusing System.Net;\nusing Microsoft.SharePoint;\n\n// Assume you have a SharePoint site URL and credentials\nstring siteUrl = "https://your-sharepoint-site.com";\nstring username = "your-username";\nstring password = "your-password";\n\n// Create a new instance of the Lists web service\nLists listsService = new Lists();\nlistsService.Url = siteUrl + "/_vti_bin/lists.asmx";\nlistsService.Credentials = new NetworkCredential(username, password);\n\n// Create a new instance of the File web service\nFile fileService = new File();\nfileService.Url = siteUrl + "/_vti_bin/file.asmx";\nfileService.Credentials = new NetworkCredential(username, password);\n\n// Read the HTML output from Sandcastle\nstring htmlOutput = File.ReadAllText("path/to/sandcastle/output.html");\n\n// Create a new list item\nXmlNode listItem = listsService.AddListFromWebPartPagesDocument(\n    "YourListName",\n    "YourListItemName",\n    htmlOutput,\n    "text/html");\n\n// Upload the HTML file to SharePoint\nfileService.UploadFile("YourFileLeafName", htmlOutput);\n\n// Clean up\nlistsService.Dispose();\nfileService.Dispose();