using Renci.SshNet;\n\nclass ScpExample\n{\n    public void UploadFile()\n    {\n        using (var client = new SftpClient("hostname", "username", "password"))\n        {\n            client.Connect();\n\n            using (var fileStream = new FileStream("localfile.txt", FileMode.Open))\n            {\n                client.UploadFile(fileStream, "/remote/path/file.txt");\n            }\n\n            client.Disconnect();\n        }\n    }\n\n    public void DownloadFile()\n    {\n        using (var client = new SftpClient("hostname", "username", "password"))\n        {\n            client.Connect();\n\n            using (var fileStream = new FileStream("localfile.txt", FileMode.Create))\n            {\n                client.DownloadFile("/remote/path/file.txt", fileStream);\n            }\n\n            client.Disconnect();\n        }\n    }\n}