using System;\n\npublic class Program\n{\n    public static uint IpToInteger(string ip)\n    {\n        string[] octets = ip.Split('.');\n        if (octets.Length != 4)\n        {\n            throw new ArgumentException("Invalid IP address format", nameof(ip));\n        }\n\n        uint ipInteger = (uint)((byte.Parse(octets[0]) << 24) + \n                                (byte.Parse(octets[1]) << 16) + \n                                (byte.Parse(octets[2]) << 8) + \n                                byte.Parse(octets[3]));\n        return ipInteger;\n    }\n\n    public static void Main()\n    {\n        string ip = "58.0.0.0";\n        uint ipInteger = IpToInteger(ip);\n        Console.WriteLine(ipInteger);  // Outputs: 973078528\n    }\n}