using Ghostscript.NET;\n\n// Create a new Ghostscript instance\nusing (var gs = new GhostscriptInstance())\n{\n    // Open the EPS file\n    using (var epsFile = new FileStream("input.eps", FileMode.Open))\n    {\n        // Create a new thumbnail image\n        using (var thumbnail = new Bitmap(100, 100))\n        {\n            // Rasterize the EPS file to the thumbnail image\n            gs.Rasterize(epsFile, thumbnail, 100, 100);\n            // Save the thumbnail to a file\n            thumbnail.Save("thumbnail.png", ImageFormat.Png);\n        }\n    }\n}