#include <Windows.h>\n#include <ImageHlp.h>\n\nint main()\n{\n    // Load the DLL\n    HMODULE hModule = LoadLibraryA("yourdll.dll");\n    if (hModule == NULL)\n    {\n        return 1;\n    }\n\n    // Get the export directory\n    PVOID pExportDir = ImageDirectoryEntryToData(hModule, TRUE, IMAGE_DIRECTORY_ENTRY_EXPORT, &dwSize);\n    if (pExportDir == NULL)\n    {\n        FreeLibrary(hModule);\n        return 1;\n    }\n\n    // Get the export directory structures\n    IMAGE_EXPORT_DIRECTORY* pExportDirStruct = (IMAGE_EXPORT_DIRECTORY*)pExportDir;\n\n    // Enumerate the export functions\n    for (DWORD i = 0; i < pExportDirStruct->NumberOfFunctions; i++)\n    {\n        // Get the function name\n        DWORD dwNameRVA = *(DWORD*)((PBYTE)hModule + pExportDirStruct->AddressOfNames + i * sizeof(DWORD));\n        char* szFunctionName = (char*)((PBYTE)hModule + dwNameRVA);\n\n        // Print the function name\n        printf("%s\n", szFunctionName);\n    }\n\n    FreeLibrary(hModule);\n    return 0;\n}