#include <Windows.h>\n\nint main()\n{\n    HKEY hKey;\n    LONG lRes = RegOpenKeyEx(HKEY_LOCAL_MACHINE, "SOFTWARE\\Microsoft\\Windows\\CurrentVersion\\Run", 0, KEY_READ, &hKey);\n    if (lRes != ERROR_SUCCESS)\n    {\n        // Handle error\n        return 1;\n    }\n\n    DWORD dwBufferSize = 1024;\n    char szValue[1024];\n    DWORD dwType;\n    lRes = RegQueryValueEx(hKey, "SomeValue", NULL, &dwType, (LPBYTE)&szValue, &dwBufferSize);\n    if (lRes != ERROR_SUCCESS)\n    {\n        // Handle error\n        RegCloseKey(hKey);\n        return 1;\n    }\n\n    // Value is stored in szValue\n\n    RegCloseKey(hKey);\n    return 0;\n}