#include <iostream>\n#include <fstream>\n\nusing namespace std;\n\nstruct PersonId {\n    int fId;\n};\n\nistream& operator>>(istream& is, PersonId& p) {\n    is >> p.fId;\n    return is;\n}\n\nint main() {\n    ifstream indata("persons.txt", ios::in);\n\n    if (!indata) {\n        cout << "Error: file could not be opened" << endl;\n        exit(1);\n    }\n\n    int n = 5;\n    PersonId* p = new PersonId[n];\n\n    for (int i = 0; i < n; i++) {\n        indata >> p[i];\n        cout << "PersonId [" << i << "] is " << p[i].fId << endl;\n    }\n\n    delete[] p;\n    return 0;\n}