#include <rocksdb/db.h>\n\n// Open the database\nrocksdb::DB* db;\nrocksdb::Options options;\noptions.create_if_missing = true;\nrocksdb::Status s = rocksdb::DB::Open(options, "/path/to/db", &db);\n\n// Write an object's state\nstd::string key = "object_key";\nstd::string value = "object_value";\ns = db->Put(rocksdb::WriteOptions(), key, value);\n\n// Read an object's state\nstd::string value_read;\ns = db->Get(rocksdb::ReadOptions(), key, &value_read);\n\n// Close the database\ndelete db;