#import "YourCSharpApp.tlb" // import the C# app's type library\n\n// Get an instance of the COM interface\nIOverlayCallback* callback = NULL;\nHRESULT hr = CoCreateInstance(__uuidof(OverlayCallback), NULL, CLSCTX_INPROC_SERVER, IID_PPV_ARGS(&callback));\nif (SUCCEEDED(hr))\n{\n    // Call the ShouldShowOverlay method\n    BSTR filePath = SysAllocString(L"C:\\Path\\To\\File.txt");\n    VARIANT_BOOL shouldShowOverlay;\n    hr = callback->ShouldShowOverlay(filePath, &shouldShowOverlay);\n    SysFreeString(filePath);\n    if (SUCCEEDED(hr) && shouldShowOverlay)\n    {\n        // Show the overlay\n    }\n    callback->Release();\n}