#include <sstream>\n#include <string>\n\nstd::string createQuery(const std::string& tableName, const std::string& columnName) {\n    std::ostringstream oss;\n    oss << "SELECT * FROM " << tableName << " WHERE " << columnName << " = ?";\n    return oss.str();\n}\n\nint main() {\n    std::string tableName = "users";\n    std::string columnName = "username";\n    std::string query = createQuery(tableName, columnName);\n    // ...\n}