#include <Windows.h>\n\nint main() {\n    HANDLE hDir = CreateFileW(L"C:\\path\\to\\directory", GENERIC_READ, FILE_SHARE_READ, NULL, OPEN_EXISTING, FILE_FLAG_BACKUP_SEMANTICS, NULL);\n    if (hDir == INVALID_HANDLE_VALUE) {\n        return 1;\n    }\n\n    FILE_NOTIFY_INFORMATION buffer[1024];\n    DWORD bytesReturned;\n    while (ReadDirectoryChangesW(hDir, buffer, sizeof(buffer), TRUE, FILE_NOTIFY_CHANGE_FILE_NAME, &bytesReturned, NULL, NULL)) {\n        for (int i = 0; i < bytesReturned / sizeof(FILE_NOTIFY_INFORMATION); i++) {\n            if (buffer[i].Action == FILE_ACTION_ADDED) {\n                printf("New file created: %ws\n", buffer[i].FileName);\n            }\n        }\n    }\n\n    CloseHandle(hDir);\n    return 0;\n}