#include <Windows.h>\n\n// Define the hook procedure\nLRESULT CALLBACK CreateFileHook(int nCode, WPARAM wParam, LPARAM lParam)\n{\n    if (nCode == HC_ACTION)\n    {\n        // Get the file name from the CreateFile parameters\n        LPCREATEFILEA pCreateFile = (LPCREATEFILEA)lParam;\n        const char* fileName = pCreateFile->lpFileName;\n\n        // Check if the file name matches the one you're interested in\n        if (strcmp(fileName, "logB.log") == 0)\n        {\n            // Handle created, do something...\n            OutputDebugStringA("Handle created to logB.log\n");\n        }\n    }\n    return CallNextHookEx(NULL, nCode, wParam, lParam);\n}\n\nint main()\n{\n    // Install the hook\n    HHOOK hHook = SetWindowsHookEx(WH_CBT, CreateFileHook, NULL, GetCurrentThreadId());\n    if (hHook == NULL)\n    {\n        // Handle error...\n    }\n\n    // Run your application as usual...\n\n    // Uninstall the hook when you're done\n    UnhookWindowsHookEx(hHook);\n    return 0;\n}