#include <Windows.h>\n\nint main() {\n    const char* filePath = "C:\\path\\to\\file.txt";\n    WIN32_FILE_ATTRIBUTE_DATA fileData;\n\n    if (GetFileAttributesExA(filePath, GetFileExInfoStandard, &fileData)) {\n        // Get file size\n        DWORD fileSize = fileData.nFileSizeLow | (fileData.nFileSizeHigh << 32);\n        printf("File size: %lu bytes\n", fileSize);\n\n        // Get file creation date and time\n        SYSTEMTIME systemTime;\n        FileTimeToSystemTime(&fileData.ftCreationTime, &systemTime);\n        printf("File creation date: %02d/%02d/%04d %02d:%02d:%02d\n",\n               systemTime.wMonth, systemTime.wDay, systemTime.wYear,\n               systemTime.wHour, systemTime.wMinute, systemTime.wSecond);\n    } else {\n        printf("Failed to retrieve file attributes\n");\n    }\n\n    return 0;\n}