#include <Windows.h>\n#include <Iphlpapi.h>\n\nint main() {\n    PIP_ADAPTER_INFO pAdapterInfo = NULL;\n    DWORD dwSize = 0;\n\n    // Get the adapters info\n    if (GetAdaptersInfo(pAdapterInfo, &dwSize) == ERROR_BUFFER_OVERFLOW) {\n        pAdapterInfo = (PIP_ADAPTER_INFO)HeapAlloc(GetProcessHeap(), 0, dwSize);\n        if (GetAdaptersInfo(pAdapterInfo, &dwSize) == NO_ERROR) {\n            // Iterate through the adapters and find the primary Ethernet adapter\n            for (PIP_ADAPTER_INFO pAdapter = pAdapterInfo; pAdapter != NULL; pAdapter = pAdapter->Next) {\n                if (pAdapter->Type == MIB_IF_TYPE_ETHERNET) {\n                    // Get the MAC address\n                    BYTE* pMacAddress = pAdapter->Address;\n                    // process the MAC address...\n                    break;\n                }\n            }\n        }\n    }\n\n    // Free the adapters info\n    if (pAdapterInfo != NULL) {\n        HeapFree(GetProcessHeap(), 0, pAdapterInfo);\n    }\n\n    return 0;\n}