const int ledpin = 9;  // light connected to digital pin 9\n\nvoid setup() {\n  Serial.begin(9600);\n}\n\nvoid loop() {\n  inout(50, 30, ledpin);\n}\n\nvoid inout(int inDelay, int outDelay, int ledpin) {\n  static int value = 0;\n  static long previousmil = 0;\n  static int done = 0;\n\n  if (millis() - previousmil > inDelay) {\n    if (value <= 255) {\n      analogWrite(ledpin, value);\n      value += 5;\n    }\n    previousmil = millis();\n  }\n\n  if (value >= 255) {\n    done = 1;\n  }\n\n  if (millis() - previousmil > outDelay && done == 1) {\n    if (value > 0) {\n      analogWrite(ledpin, value);\n      value -= 5;\n    }\n    previousmil = millis();\n  }\n}