// Create an instance of Excel.Application\nCLSID clsid;\nHRESULT hr = CLSIDFromProgID(L"Excel.Application", &clsid);\nif (SUCCEEDED(hr)) {\n    IDispatch* pApp;\n    hr = CoCreateInstance(clsid, NULL, CLSCTX_LOCAL_SERVER, IID_PPV_ARGS(&pApp));\n    if (SUCCEEDED(hr)) {\n        // Get the workbooks collection\n        IDispatch* pWorkbooks;\n        hr = pApp->GetIDsOfNames(IID_NULL, &L"Workbooks", 1, LOCALE_USER_DEFAULT, &pWorkbooks);\n        if (SUCCEEDED(hr)) {\n            // Open the target file and get the worksheet\n            IDispatch* pWorksheet;\n            hr = pWorkbooks->Invoke(0, IID_NULL, LOCALE_USER_DEFAULT, DISPATCH_METHOD, &DISPPARAMS{ L"Open", L"your_file.xlsx" }, &pWorksheet, NULL, NULL);\n            if (SUCCEEDED(hr)) {\n                // Use the worksheet object\n                // ...\n                pWorksheet->Release();\n            }\n            pWorkbooks->Release();\n        }\n        pApp->Release();\n    }\n}