#include <Windows.h>\n\nint main() {\n    HANDLE hPipe = CreateNamedPipe(\n        L"\\\\.\\pipe\\my_pipe", // pipe name\n        PIPE_ACCESS_DUPLEX, // read/write access\n        PIPE_TYPE_BYTE | PIPE_READMODE_BYTE | PIPE_WAIT, // byte-type pipe, blocking read\n        1, // max instances\n        0, // output buffer size\n        0, // input buffer size\n        0, // default timeout\n        NULL // security attributes\n    );\n\n    if (hPipe == INVALID_HANDLE_VALUE) {\n        // handle error\n    }\n\n    // Wait for the console app to connect to the pipe\n    ConnectNamedPipe(hPipe, NULL);\n\n    // Read the pipe's contents\n    char buffer[1024];\n    DWORD bytesRead = 0;\n    while (ReadFile(hPipe, buffer, 1024, &bytesRead, NULL)) {\n        // process the data in the buffer\n        // e.g., parse the parameters\n    }\n\n    // Close the pipe handle\n    CloseHandle(hPipe);\n\n    return 0;\n}