const int ledPins[] = {9, 10, 11};  // array of LED pins\nstatic int values[sizeof(ledPins) / sizeof(ledPins[0])];  // array of values\nstatic long previousmils[sizeof(ledPins) / sizeof(ledPins[0])];  // array of previousmils\n\nvoid setup() {\n  Serial.begin(9600);\n}\n\nvoid loop() {\n  inout(50, 30);\n}\n\nvoid inout(int inDelay, int outDelay) {\n  for (int i = 0; i < sizeof(ledPins) / sizeof(ledPins[0]); i++) {\n    if (millis() - previousmils[i] > inDelay) {\n      if (values[i] <= 255) {\n        analogWrite(ledPins[i], values[i]);\n        values[i] += 5;\n      }\n      previousmils[i] = millis();\n    }\n\n    if (values[i] >= 255) {\n      // fade out\n      if (millis() - previousmils[i] > outDelay) {\n        if (values[i] > 0) {\n          analogWrite(ledPins[i], values[i]);\n          values[i] -= 5;\n        }\n        previousmils[i] = millis();\n      }\n    }\n  }\n}