// Primary object (Component)\nclass Component : public IComponent, public IDelegate\n{\npublic:\n    // IComponent methods\n    void DoSomething() { /* implementation */ }\n\n    // IDelegate methods\n    HRESULT QueryInterface(REFIID riid, void** ppvObject)\n    {\n        if (riid == IID_IExtension)\n        {\n            // Delegate to secondary object (Extension)\n            return extension_->QueryInterface(riid, ppvObject);\n        }\n        return E_NOINTERFACE;\n    }\n\nprivate:\n    Extension* extension_;\n};\n\n// Secondary object (Extension)\nclass Extension : public IExtension\n{\npublic:\n    // IExtension methods\n    void DoSomethingElse() { /* implementation */ }\n};