#include <Windows.h>\n#include <ole2.h>\n#import "Excel.tlb" named_guids  // Import the Excel type library\n\nint main() {\n    // Initialize the COM library\n    CoInitialize(NULL);\n\n    // Create an instance of the Excel application\n    Excel::_ApplicationPtr pExcelApp;\n    HRESULT hr = pExcelApp.CreateInstance(__uuidof(Excel::Application));\n\n    if (SUCCEEDED(hr)) {\n        // Get the interface pointer\n        Excel::_WorkbookPtr pWorkbook = pExcelApp->Workbooks->Add();\n\n        // Perform basic operations...\n        pWorkbook->SaveAs("C:\\Example.xlsx");\n\n        // Release resources\n        pWorkbook.Release();\n        pExcelApp.Release();\n    }\n\n    // Uninitialize the COM library\n    CoUninitialize();\n    return 0;\n}