#include <atlbase.h>\n#include <atlcom.h>\n\n// Import the application's type library\n#import "Application.tlb"\n\nint main()\n{\n    // Initialize COM\n    CoInitialize(NULL);\n\n    // Create an instance of the application's COM interface\n    CComPtr<IApplication> pApp;\n    HRESULT hr = pApp.CoCreateInstance(CLSID_Application);\n\n    if (SUCCEEDED(hr))\n    {\n        // Call a method on the interface\n        pApp->Method();\n    }\n\n    // Release the interface\n    pApp.Release();\n\n    // Uninitialize COM\n    CoUninitialize();\n\n    return 0;\n}