#include <iostream>\n#include <cv.h>\n#include <highgui.h>\n#include <cxcore.h>\n\nint main(int argc, char** argv)\n{\n\n\tcvNamedWindow("video", 1);\n\n\tCvCapture* capture = cvCreateCameraCapture(0);\n\n\twhile(true)\n\t{\n\t\tIplImage* frame = cvQueryFrame(capture);\n\n\t\tif(!frame) break;\n\n\t\tcvShowImage("video", frame);\n\n\t\tint c = cvWaitKey(10);\n\t\tif(c == 27) break;\n\t}\n\n\tcvReleaseCapture(&capture);\n\tcvDestroyWindow("video");\n\n\treturn 0;\n}