// MyDll.h\nextern "C" __declspec(dllexport) void ShowDialog();\n\n// MyDll.cpp\n#include "MyDll.h"\n#include "resource.h"\n\nBEGIN_MESSAGE_MAP(MyDll, CWinApp)\nEND_MESSAGE_MAP()\n\nBOOL MyDll::InitInstance()\n{\n    AfxGetInstanceHandle();\n    AfxInitRichEdit();\n    return TRUE;\n}\n\nvoid ShowDialog()\n{\n    AfxGetResourceHandle();\n    CDialog dlg(IDD_MYDIALOG);\n    dlg.DoModal();\n    CButton* pButton = (CButton*)dlg.GetDlgItem(IDC_MYBUTTON);\n    if (pButton)\n    {\n        pButton->SetWindowText(_T("New button text"));\n    }\n}