// main.cpp\n#include <Windows.h>\n#include "myclass.h"\n\nint main() {\n    HMODULE hModule = LoadLibraryA("mydll.dll");\n    if (!hModule) {\n        // error handling\n    }\n\n    MyClass* (*pConstructor)() = (MyClass* (*)())GetProcAddress(hModule, "?0MyClass@QEAA@XZ");\n    if (!pConstructor) {\n        // error handling\n    }\n\n    MyClass* pObj = pConstructor();\n    pObj->myMethod();\n\n    delete pObj;\n    FreeLibrary(hModule);\n    return 0;\n}