// STA example\nCoInitialize(NULL); // Initialize STA\nIThread *thread = CreateThread();\nthread->Start();\n// Only one thread can execute at a time in STA\n\n// MTA example\nCoInitializeEx(COINIT_MULTITHREADED); // Initialize MTA\nIThread *thread1 = CreateThread();\nIThread *thread2 = CreateThread();\nthread1->Start();\nthread2->Start();\n// Multiple threads can execute concurrently in MTA