#include <windows.h>\n#include <dshow.h>\n\nint main()\n{\n    IFilterCategory *pCategory = NULL;\n    HRESULT hr = CoCreateInstance(CLSID_VideoCompressorCategory, NULL, CLSCTX_INPROC_SERVER, IID_PPV_ARGS(&pCategory));\n    if (SUCCEEDED(hr))\n    {\n        IEnumFilters *pEnum;\n        hr = pCategory->EnumFilters(&pEnum);\n        if (SUCCEEDED(hr))\n        {\n            IBaseFilter *pFilter;\n            while (pEnum->Next(1, &pFilter, NULL) == S_OK)\n            {\n                FILTER_INFO FilterInfo;\n                hr = pFilter->QueryFilterInfo(&FilterInfo);\n                if (SUCCEEDED(hr))\n                {\n                    wprintf(L"%s\n", FilterInfo.achName);\n                }\n                pFilter->Release();\n            }\n            pEnum->Release();\n        }\n        pCategory->Release();\n    }\n\n    return 0;\n}