#include <Windows.h>\n\nint main()\n{\n    HANDLE hConsole = GetStdHandle(STD_OUTPUT_HANDLE);\n    COORD coord = { 0, 0 }; // coordinates (X, Y)\n\n    // Write initial text\n    printf("Running... 00%% complete\n");\n    printf("Buffer size: 0000 bytes\n");\n\n    // Update text\n    for (int i = 1; i <= 100; i++)\n    {\n        coord.X = 0; coord.Y = 0;\n        SetConsoleCursorPosition(hConsole, coord);\n        printf("Running... %02d%% complete\n", i);\n\n        coord.X = 0; coord.Y = 1;\n        SetConsoleCursorPosition(hConsole, coord);\n        printf("Buffer size: %04d bytes\n", i * 10);\n        Sleep(50); // simulate work\n    }\n\n    return 0;\n}