// HelloMFC.cpp\n#include <afxwin.h>\n\nclass CHelloMFCApp : public CWinApp\n{\npublic:\n    CHelloMFCApp();\n    ~CHelloMFCApp();\n\n    virtual BOOL InitInstance();\n};\n\nCHelloMFCApp::CHelloMFCApp()\n{\n}\n\nCHelloMFCApp::~CHelloMFCApp()\n{\n}\n\nBOOL CHelloMFCApp::InitInstance()\n{\n    // Create a dialog-based app\n    CDialog dlg(IDD_HELLOMFC_DIALOG);\n    dlg.DoModal();\n\n    return TRUE;\n}