#include <iostream>\n#include <string>\n#include <cstdint>\n\n// Function to check if a character is a Unicode character\nbool isUnicodeChar(char c) {\n    // UTF-8 Unicode characters have the most significant bit set\n    return (c & 0x80) != 0;\n}\n\n// Function to convert a Unicode character to hexadecimal\nstd::string unicodeToHex(const std::string& str) {\n    std::string hexStr;\n    for (char c : str) {\n        if (isUnicodeChar(c)) {\n            // Convert to hexadecimal and append to the result string\n            char hex[5];\n            sprintf(hex, "\\x%02x", static_cast<uint8_t>(c));\n            hexStr += hex;\n        } else {\n            // Non-Unicode characters are appended as is\n            hexStr += c;\n        }\n    }\n    return hexStr;\n}\n\nint main() {\n    // Example string with Unicode characters\n    std::string str = "Hello, àçé!";\n\n    // Check if the string contains Unicode characters and print in hexadecimal\n    std::cout << "Original string: " << str << std::endl;\n    std::cout << "String with Unicode characters in hexadecimal: " << unicodeToHex(str) << std::endl;\n\n    return 0;\n}