#include <string>\n#include <map>\n\nint main() {\n    std::map<std::string, std::string> dictionary;\n    dictionary.insert(std::make_pair("á", "a"));\n    dictionary.insert(std::make_pair("é", "e"));\n    dictionary.insert(std::make_pair("í", "i"));\n    dictionary.insert(std::make_pair("ó", "o"));\n    dictionary.insert(std::make_pair("ú", "u"));\n    dictionary.insert(std::make_pair("ñ", "n"));\n\n    std::string toReplace = "á-é-í-ó-ú-ñ-á-é-í-ó-ú-ñ";\n\n    for (const auto& pair : dictionary) {\n        size_t pos = toReplace.find(pair.first);\n        while (pos != std::string::npos) {\n            toReplace.replace(pos, pair.first.length(), pair.second);\n            pos = toReplace.find(pair.first, pos + pair.second.length());\n        }\n    }\n\n    std::cout << toReplace << std::endl;\n    return 0;\n}