#include <html-sanitizer.h>\n\nint main() {\n  // input string that might contain HTML\n  std::string input = "<script>alert('XSS')</script>";\n\n  // create an HTML sanitizer\n  html_sanitizer::Sanitizer sanitizer;\n\n  // set the allowed tags and attributes\n  sanitizer.AllowTag("p");\n  sanitizer.AllowAttribute("style");\n\n  // sanitize the input string\n  std::string sanitized = sanitizer.Sanitize(input);\n\n  // sanitized string is now safe to use\n  std::cout << sanitized << std::endl;\n\n  return 0;\n}