// Ray casting algorithm\nbool isObjectVisible(const Object& obj, const Camera& cam) {\n  // Create a ray from the camera to the object\n  Ray ray(cam.getPosition(), obj.getPosition() - cam.getPosition());\n\n  // Check for intersections with other objects\n  for (const Object& otherObj : objects) {\n    if (otherObj != obj && ray.intersects(otherObj.getBoundingBox())) {\n      return false; // Object is occluded\n    }\n  }\n\n  return true; // Object is visible\n}