#include <windows.h>\n#include <uiautomation.h>\n\nint main() {\n    // Initialize UI Automation\n    IUIAutomation* pAutomation;\n    CoCreateInstance(CLSID_CUIAutomation, NULL, CLSCTX_INPROC_SERVER, IID_PPV_ARGS(&pAutomation));\n\n    // Get the UI element under the mouse cursor\n    POINT pt;\n    GetCursorPos(&pt);\n    IUIAutomationElement* pElement;\n    pAutomation->ElementFromPoint(pt.x, pt.y, &pElement);\n\n    // Extract text from the element\n    BSTR text;\n    pElement->get_CurrentName(&text);\n\n    // Do something with the extracted text...\n    wprintf(L"Extracted text: %s\n", text);\n\n    // Clean up\n    SysFreeString(text);\n    pElement->Release();\n    pAutomation->Release();\n\n    return 0;\n}