#include <Windows.h>\n\nint main() {\n    WCHAR devicePath[] = L"\\Device\\CdRom0";\n    WCHAR driveLetter[3];\n\n    // Get the logical drive letter\n    if (QueryDosDevice(devicePath, driveLetter, 3)) {\n        // driveLetter now contains the logical drive letter (e.g. "G:")\n        // Add a backslash at the end to get the full drive path\n        driveLetter[2] = L'\\';\n        driveLetter[3] = L'\0';\n        // Now use the driveLetter variable as needed\n    } else {\n        // Handle error\n    }\n\n    return 0;\n}