#include <opencv2/opencv.hpp>\n\ncv::Mat expandPolygon(const cv::Mat& polygon, double expansionAmount) {\n  // Create a circle or square structuring element\n  cv::Mat element = cv::getStructuringElement(cv::MORPH_ELLIPSE, cv::Size(expansionAmount, expansionAmount));\n\n  // Compute the Minkowski sum\n  cv::Mat expandedPolygon;\n  cv::morphologyEx(polygon, expandedPolygon, cv::MORPH_DILATE, element);\n\n  return expandedPolygon;\n}