#include <iostream>\n#include <fstream>\n#include <sstream>\n#include <unordered_set>\n\nint main(int arg_count, char* arg_vec[]) {\n    if (arg_count != 2) {\n        std::cerr << "expected one argument" << std::endl;\n        return EXIT_FAILURE;\n    }\n\n    std::string line;\n    std::ifstream myfile(arg_vec[1]);\n\n    std::unordered_set<std::string> uniqueTags;\n\n    if (myfile.is_open()) {\n        while (std::getline(myfile, line)) {\n            std::stringstream ss(line);\n            std::string Elem;\n\n            while (ss >> Elem) {\n                uniqueTags.insert(Elem);\n            }\n        }\n        myfile.close();\n    } else {\n        std::cout << "Unable to open file";\n    }\n\n    for (const auto& tag : uniqueTags) {\n        std::cout << tag << std::endl;\n    }\n\n    return 0;\n}