// myclass.h\nclass MyClass {\npublic:\n    MyClass();\n    ~MyClass();\n    void myMethod();\n};\n\nextern "C" __declspec(dllexport) MyClass* createMyClass();\nextern "C" __declspec(dllexport) void deleteMyClass(MyClass* obj);\nextern "C" __declspec(dllexport) void myMethod(MyClass* obj);\n\n// myclass.cpp\nMyClass::MyClass() {}\nMyClass::~MyClass() {}\nvoid MyClass::myMethod() {}\n\nMyClass* createMyClass() {\n    return new MyClass();\n}\n\nvoid deleteMyClass(MyClass* obj) {\n    delete obj;\n}\n\nvoid myMethod(MyClass* obj) {\n    obj->myMethod();\n}