// features/file_io.feature\nFeature: File I/O\n  Scenario: Write to file\n    Given a temporary file\n    When I write "Hello World" to the file\n    Then the file should contain "Hello World"\n\n// step_definitions/file_io_steps.cpp\n#include <fstream>\n#include <string>\n\nusing namespace std;\n\n GIVEN("a temporary file") {\n   // Create a temporary file\n   tmp_file_ = tmpnam(nullptr);\n   ofstream file(tmp_file_);\n   file.close();\n }\n\n WHEN("I write {string} to the file") {\n   string content = "Hello World";\n   ofstream file(tmp_file_);\n   file << content;\n   file.close();\n }\n\n THEN("the file should contain {string}") {\n   ifstream file(tmp_file_);\n   string content;\n   file >> content;\n   assert(content == "Hello World");\n }