#include <oaidl.h>\n\n// Assume pDisp is a pointer to the IDispatch interface of the COM object\nIDispatch* pDisp = ...;\n\n// Get the ITypeInfo interface\nITypeInfo* pTypeInfo;\nHRESULT hr = pDisp->GetTypeInfo(0, LOCALE_SYSTEM_DEFAULT, &pTypeInfo);\nif (SUCCEEDED(hr))\n{\n    // Enumerate the members\n    TYPEATTR* pTypeAttr;\n    hr = pTypeInfo->GetTypeAttr(&pTypeAttr);\n    if (SUCCEEDED(hr))\n    {\n        // Iterate over the members\n        for (int i = 0; i < pTypeAttr->cFuncs; i++)\n        {\n            FUNCDESC* pFuncDesc;\n            hr = pTypeInfo->GetFuncDesc(i, &pFuncDesc);\n            if (SUCCEEDED(hr))\n            {\n                // Get the member name\n                BSTR bstrName;\n                hr = pTypeInfo->GetDocumentation(pFuncDesc->memid, &bstrName, NULL, NULL, NULL);\n                if (SUCCEEDED(hr))\n                {\n                    printf("%S\n", bstrName);\n                    SysFreeString(bstrName);\n                }\n\n                // Get the member parameters\n                for (int j = 0; j < pFuncDesc->cParams; j++)\n                {\n                    PARAMDESC* pParamDesc;\n                    hr = pTypeInfo->GetParamDesc(pFuncDesc->memid, j, &pParamDesc);\n                    if (SUCCEEDED(hr))\n                    {\n                        // Get the parameter name\n                        BSTR bstrParamName;\n                        hr = pTypeInfo->GetDocumentation(pParamDesc->paramdesc.parmdesc.name, &bstrParamName, NULL, NULL, NULL);\n                        if (SUCCEEDED(hr))\n                        {\n                            printf("  %S\n", bstrParamName);\n                            SysFreeString(bstrParamName);\n                        }\n                    }\n                }\n            }\n        }\n    }\n}