#include <boost/network/uri.hpp>\n#include <boost/network/html.hpp>\n\nint main() {\n  // Create an HTML document\n  boost::network::html::document doc;\n\n  // Add a paragraph\n  doc << boost::network::html::paragraph("Hello, World!");\n\n  // Add a link\n  doc << boost::network::html::anchor("https://www.example.com", "Example");\n\n  // Output the HTML\n  std::cout << doc << std::endl;\n\n  return 0;\n}