#include "Poco/Net/HTMLWriter.h"\n\nint main() {\n  Poco::Net::HTMLWriter writer;\n\n  // Start an HTML document\n  writer.startDocument("text/html");\n\n  // Add a paragraph\n  writer.startElement("p");\n  writer.characters("Hello, World!");\n  writer.endElement();\n\n  // Add a link\n  writer.startElement("a");\n  writer.attribute("href", "https://www.example.com");\n  writer.characters("Example");\n  writer.endElement();\n\n  // Output the HTML\n  std::cout << writer.toString() << std::endl;\n\n  return 0;\n}