#include <Eigen/Dense>\n#include <iostream>\n\nint main() {\n    // Your 4x4 matrix in column-major order\n    float matrixData[16] = {\n        1.0f, 2.0f, 3.0f, 4.0f,\n        5.0f, 6.0f, 7.0f, 8.0f,\n        9.0f, 10.0f, 11.0f, 12.0f,\n        13.0f, 14.0f, 15.0f, 16.0f\n    };\n\n    // Map the data to an Eigen Matrix4f\n    Eigen::Matrix4f matrix;\n    matrix = Eigen::Map<Eigen::Matrix4f>(matrixData).transpose();\n\n    // Check if the matrix is invertible\n    if (matrix.determinant() == 0) {\n        std::cerr << "Matrix is not invertible." << std::endl;\n        return 1;\n    }\n\n    // Calculate the inverse\n    Eigen::Matrix4f inverse = matrix.inverse();\n\n    // Print the inverse\n    std::cout << "Inverse of the matrix:\n" << inverse << std::endl;\n\n    return 0;\n}