#include "yaml-cpp/yaml.h"\n\nint main() {\n    YAML::Node node = YAML::Load("{ key: value }");\n    std::string key = node["key"].as<std::string>();\n    std::cout << key << std::endl;  // Output: value\n\n    YAML::Emitter emitter;\n    emitter << YAML::BeginMap;\n    emitter << YAML::Key << "key";\n    emitter << YAML::Value << "value";\n    emitter << YAML::EndMap;\n    std::cout << emitter.c_str() << std::endl;  // Output: {key: value}\n\n    return 0;\n}