#include <msvapi.h>\n\n// ...\n\n// Create a new message entry\nCMsvEntry* entry = CMsvEntry::NewL();\nentry->SetType(KMsvMessageEntry);\nentry->SetVisible(ETrue);\n\n// Set the message text and other properties\nentry->SetTextL(aMessageText);\nentry->SetSenderAddressL(aSenderAddress);\n\n// Create a new message session\nCMsvSession* session = CMsvSession::NewL();\nsession->AddEntryL(entry);\n\n// Add the message to the Inbox\nsession->SaveChangesL();\n\n// Clean up\ndelete session;\ndelete entry;