#include <cmath>\n\nstruct Point {\n    double x, y;\n};\n\nPoint rotatePoint(const Point& point, double angle, const Point& pivot) {\n    double cosAngle = std::cos(angle);\n    double sinAngle = std::sin(angle);\n\n    double translatedX = point.x - pivot.x;\n    double translatedY = point.y - pivot.y;\n\n    double rotatedX = translatedX * cosAngle - translatedY * sinAngle;\n    double rotatedY = translatedX * sinAngle + translatedY * cosAngle;\n\n    return {rotatedX + pivot.x, rotatedY + pivot.y};\n}