#include <Windows.h>\n#include <iostream>\n\n// ...\n\nUINT run1(LPVOID param)\n{\n    // ...\n}\n\nUINT run2(LPVOID param)\n{\n    // ...\n}\n\nvoid main(int argc, char* argv[])\n{\n    // ...\n\n    CWinThread* pThread1 = AfxBeginThread(&run1, NULL, THREAD_PRIORITY_NORMAL);\n    CWinThread* pThread2 = AfxBeginThread(&run2, NULL, THREAD_PRIORITY_BELOW_NORMAL);\n\n    Sleep(3 * 1000);\n\n    bStop = TRUE;\n\n    // Get the thread cycle times\n    ULONG64 cycleTime1, cycleTime2;\n    QueryThreadCycleTime(*pThread1, &cycleTime1);\n    QueryThreadCycleTime(*pThread2, &cycleTime2);\n\n    // Get the performance frequency\n    LARGE_INTEGER frequency;\n    QueryPerformanceFrequency(&frequency);\n\n    // Convert cycle times to seconds\n    double time1 = (double)cycleTime1 / frequency.QuadPart;\n    double time2 = (double)cycleTime2 / frequency.QuadPart;\n\n    std::cout << "K1=" << k1 << "\n";\n    std::cout << "K2=" << k2 << "\n";\n    std::cout << "Thread1 Time=" << time1 << "sec\n";\n    std::cout << "Thread2 Time=" << time2 << "sec\n";\n    std::cout << "Exit\n";\n}