#include <windows.h>\n#include <winternl.h>\n#include <evntrace.h>\n\nint main() {\n    // Create a trace session\n    TRACEHANDLE hTrace = StartTrace(NULL, L"MyTrace", 0, 0, 0, 0, 0, 0);\n\n    // Enable tracing for synchronization objects\n    EnableTraceEx(hTrace, L"SYNC_OBJECT", 0, 0, 0);\n\n    // Log events\n    EventWriteString(hTrace, L"Event - testevt", L"Thread #5421 Signal Event - testevt");\n\n    // Stop the trace session\n    StopTrace(hTrace, L"MyTrace", 0);\n\n    return 0;\n}