#include <QSettings>\n#include <QString>\n#include <QDebug>\n\nvoid storeStringToRegistry(const QString& key, const QString& value) {\n    QByteArray encodedValue = value.toUtf8().toBase64();\n    QString encodedString(encodedValue);\n    \n    QSettings settings("YourCompany", "YourApp");\n    settings.setValue(key, encodedString);\n}\n\nQString retrieveStringFromRegistry(const QString& key) {\n    QSettings settings("YourCompany", "YourApp");\n    QString encodedString = settings.value(key).toString();\n    \n    QByteArray encodedValue = encodedString.toUtf8();\n    return QByteArray::fromBase64(encodedValue);\n}\n\nint main() {\n    QString testString = "Path\\With\\Backslashes";\n    QString registryKey = "Test/ RegistryKey";\n    \n    storeStringToRegistry(registryKey, testString);\n    \n    QString retrievedString = retrieveStringFromRegistry(registryKey);\n    \n    qDebug() << "Original String:" << testString;\n    qDebug() << "Retrieved String:" << retrievedString;\n    \n    return 0;\n}