// Example usage:\n#include <QxORM.h>\n\nclass User : public QxORM::IQxBean {\npublic:\n    User() : id(0) {}\n    int id;\n    QString name;\n};\n\nint main() {\n    QxORM::QxSqlDatabase db = QxORM::QxSqlDatabase::addDatabase("QSQLITE");\n    db.setDatabaseName("mydatabase.db");\n    QxORM::QxSession session(db);\n    QxORM::QxQuery q = session.query();\n    q << "SELECT * FROM users";\n    QxORM::QxResult r = q.execute();\n    while (r.next()) {\n        User user;\n        r >> user.id >> user.name;\n        std::cout << "Name: " << user.name.toStdString() << std::endl;\n    }\n    return 0;\n}