#include <Windows.h>\n#include <mshtml.h>\n#include <shdocvw.h>\n\n// ...\n\nBOOL CALLBACK EnumWindowsProc(HWND hWnd, LPARAM lParam)\n{\n    WCHAR className[256];\n    WCHAR title[256];\n\n    GetClassName(hWnd, className, 256);\n    GetWindowText(hWnd, title, 256);\n\n    if (wcscmp(className, L"IEFrame") == 0 && wcscmp(title, L"Internet Explorer") == 0)\n    {\n        DWORD processId;\n        GetWindowThreadProcessId(hWnd, &processId);\n\n        HANDLE hProcess = OpenProcess(PROCESS_QUERY_INFORMATION | PROCESS_VM_READ, FALSE, processId);\n        if (hProcess != NULL)\n        {\n            HMODULE hModule = GetModuleHandle(L"iexplore.exe");\n            if (hModule != NULL)\n            {\n                IWebBrowser2* pWebBrowser = (IWebBrowser2*)GetProcAddress(hModule, "SHDocVw::IWebBrowser2");\n                if (pWebBrowser != NULL)\n                {\n                    // Use the pWebBrowser pointer to interact with the browser\n                }\n            }\n        }\n    }\n\n    return TRUE;\n}\n\nint main()\n{\n    EnumWindows(EnumWindowsProc, 0);\n\n    return 0;\n}